import React from 'react';
import { connect } from 'react-redux';
import { fetchImages } from '../../actions/imageActions';

class LoadMoreButton extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render() {
    debugger;
    let canLoadMore = (this.props.images.length % 25 === 0 && this.props.images.length !== 0);


    return (
      <div>{ canLoadMore ? <button type="submit">Submit</button> : null }</div>
    )
  }
}

function mapStateToProps(state) {
  const { images, batchId, page } = state.imageState;

  return {
    images: images,
    batchId: batchId,
    page: page
  }
}

export default connect(mapStateToProps, { fetchImages })(LoadMoreButton)
