import React from 'react';
import { connect } from 'react-redux';
import { fetchImages } from '../../actions/imageActions';

class LoadMoreButton extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render() {
    const { images, fetchImages, batchId } = this.props;
    let canLoadMore = (images.length % 25 === 0 && images.length !== 0);

    return (
      <div>
        { canLoadMore ? <button type="submit" onClick={() => fetchImages(batchId + 1)}>Load More Images</button> : null }
      </div>
    )
  }
}

function mapStateToProps(state) {
  const { images, batchId, page } = state.imageState;

  return {
    images: images,
    batchId: batchId,
    page: page
  }
}

export default connect(mapStateToProps, { fetchImages })(LoadMoreButton)
