import React from 'react';

import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchImages } from '../../actions/imageActions';

class Gallery extends React.Component {
  constructor(props) {
    super(props);
  }

  renderImages() {
    debugger;
    if (!this.props.images || this.props.images.length == 0) {
      return <div className="no-images">There are no images to display!</div>
    } else {
      return this.props.images.map((url, key) => {
        return <img className="picture" href={url} key={key} />
      })
    }
  }

  render() {
    return (
      <div className="gallery">
        { this.renderImages() }
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { images, batchId, page } = state.imageState;
  return {
    images: images,
    batchId: batchId,
    page: page
  }
}

export default connect(mapStateToProps, {fetchImages})(Gallery);
