import React from 'react';

import './App.css';
import { postCsv } from '../../services/CsvService.js';
import FileInput from '../FileInput/FileInput';
import Gallery from '../Gallery/Gallery';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null,
      pictures: null,
      batchId: null
    };
  }


  handleSubmit(event) {
    event.preventDefault();
    let file = this.fileInput.current.files[0];


    if (file) {
      this.setState({file: file});
      let payload = new FormData();//{file_name: file.name, content: file}
      payload.append("file", file);
      postCsv(payload).then(response => {
        this.setState({
          pictures: response.data.pictures,
          batchId: response.data.batch_id
        })
      });
    } else {
      alert("there is no file submitted!");
    }
  }

  render() {
    const { pictures } = this.state;
    return (
      <div className="App">
        <FileInput file={this.state.file} pictures={this.state.pictures} batchId={this.state.batchId} />  
        <Gallery />
      </div>
    );
  }
}

export default App;
